import React from 'react';
import { useState } from 'react';

const lists = [
    {
        "id": 1,
        "name": "Vi"
    },
    {
        "id": 2,
        "name": "Vinh"
    },
    {
        "id": 3,
        "name": "Thục Vi"
    }
]



function Todo(props) {
    const [checked, setChecked] = useState();
    const [name, setName] = useState('');

    const handleInput = () => {
        setName(name)
    }

    const handleCheckedChange = () => {
        console.log(checked)
    }

    return (
        <div style={{ padding: 32 }}>
            <div>
                <span style={{ marginRight: 32 }}>Họ và tên</span>
                <input type="input" placeholder='Nhập tên của bạn'
                    onChange={() => handleInput}
                />
            </div>
            {
                lists.map(infor => (
                    <div key={infor.id} >
                        <input
                            type="checkbox"
                            checked={checked === infor.id}
                            onChange={() => setChecked(infor.id)}
                        />{infor.name}
                    </div>
                ))


            }
            <button type='button' onClick={handleCheckedChange} > Submit</button>
        </div >
    );
}

export default Todo;
