import React, { useState } from "react";


const lists = [
    {
        "id": 1,
        "name": "Vi"
    },
    {
        "id": 2,
        "name": "Vinh"
    },
    {
        "id": 3,
        "name": "Thục Vi"
    }
]

function Todo(props) {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [checked, setChecked] = useState();
    // const [name, setName] = useState('');



    const Submit = (event) => {
        event.preventDefault();
        console.log({ name, email, phoneNumber });
    };

    return (
        <div>
            <form
                onSubmit={Submit}
                style={{
                    display: "flex",
                    width: 400,
                    height: 200,
                    textAlign: "center",
                    alignItems: "center",
                    flexDirection: "column",
                    border: "1px solid black",
                }}
            >
                <div
                    style={{
                        display: "flex",
                        flexDirection: "column",
                        justifyContent: "space-around",
                        flexShrinking: 0,
                        flex: 4,
                        paddingLeft: "16px",
                        paddingRight: "16px",
                    }}
                >
                    <input
                        type="text"
                        placeholder="Enter your name"
                        onChange={(e) => {
                            setName(e.target.value);
                        }}
                    />
                    <input
                        type="text"
                        placeholder="Enter your email"
                        onChange={(e) => {
                            setEmail(e.target.value);
                        }}
                    />
                    <input
                        type="text"
                        placeholder="Enter your phone number"
                        onChange={(e) => {
                            setPhoneNumber(e.target.value);
                        }}
                    />
                </div>
                <div
                    style={{
                        display: 'flex',
                        alignItems: "center",
                        textAlign: "center",
                        justifyContent: 'center',
                        flex: 1,
                        padding: 16,
                        margin: "auto",
                    }}
                >
                    <span style={{ marginRight: 32 }}>Họ và tên</span>
                    <input type="input" placeholder='Nhập tên của bạn'
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>
                {
                    lists.map(infor => (
                        <div key={infor.id} >
                            <input
                                type="checkbox"
                                checked={checked === infor.id}
                                onChange={() => setChecked(infor.id)}
                            />{infor.name}
                        </div>
                    ))

                }
                <button type="submit" style={{

                    flex: 1,
                }}>Submit</button>
            </form >
        </div >
    );
}

export default Todo;
