import React, { useState } from "react";


const lists = [
    {
        "id": 1,
        "name": "Vi"
    },
    {
        "id": 2,
        "name": "Vinh"
    },
    {
        "id": 3,
        "name": "Thục Vi"
    }
]

function Todo(props) {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [checked, setChecked] = useState();
    // const [name, setName] = useState('');



    const Submit = (event) => {
        event.preventDefault();
        console.log({ name, email, phoneNumber, checked });
    };

    return (
        <div>
            <form
                onSubmit={Submit}
                style={{
                    display: "flex",
                    width: 400,
                    height: 'auto',
                    textAlign: "center",
                    alignItems: "center",
                    flexDirection: "column",
                    border: "1px solid black",
                }}
            >
                <div
                    style={{
                        display: "flex",
                        flexDirection: "column",
                        justifyContent: "space-around",
                        height: 100,
                        paddingLeft: "16px",
                        paddingRight: "16px",
                    }}
                >
                    <input
                        type="text"
                        placeholder="Enter your name"
                        onChange={(e) => {
                            setName(e.target.value);
                        }}
                    />
                    <input
                        type="text"
                        placeholder="Enter your email"
                        onChange={(e) => {
                            setEmail(e.target.value);
                        }}
                    />
                    <input
                        type="text"
                        placeholder="Enter your phone number"
                        onChange={(e) => {
                            setPhoneNumber(e.target.value);
                        }}
                    />
                </div>
                <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    width: 200,
                    textAlign: 'start',
                    padding: 16,
                }}>
                    {
                        lists.map(infor => (
                            <div key={infor.id} >
                                <input
                                    type="radio"
                                    checked={checked === infor.id}
                                    onChange={() => setChecked(`${infor.id} + {infor.name}`)}
                                />{infor.name}
                            </div>
                        ))
                    }
                </div>
                <button type="submit" style={{
                    padding: 8,
                    margin: 8,
                }}>Submit</button>
            </form >
        </div >
    );
}

export default Todo;
