import React from 'react';
// import ChangeQuantity from '../ChangeQuantity/ChangeQuantity';
// import '../ChangeQuantity/ChangeQuantity.css'

import { useState } from 'react';

const item = `${process.env.PUBLIC_URL}/img/cafe_den.png`;


function CartItem(props) {

    const [counter, setCounter] = useState(0)

    const increaseCounter = () => {
        setCounter(preState => preState + 1)
        handleChange(preState)
    }

    const decreaseCounter = () => {
        setCounter((preState) => preState > 0 ? preState - 1 : 0);
    }

    return (
        <div className='CartItem'>
            <div className='CartItem-ItemHolder'>
                <img src={item} alt='item' className='CartItem-ItemHolder-Image margin-auto' />
            </div>
            <div className='CartItem-ItemHolder'>
                <p className='margin-auto'>
                    Cà Phê Đen
                </p>
            </div>
            <div className='CartItem-ItemHolder'>
                <p className='margin-auto'>
                    20000
                </p>
            </div>
            <div className='CartItem-ItemHolder margin-auto'>
                <div className='ChangeQuantity margin-auto'>
                    <div className='ChangeQuantity ' >
                        <div className='sub height-30px'
                            onClick={decreaseCounter}>
                            <i class="fa-solid fa-minus"></i>
                        </div>
                        <p className='Quantity height-30px'>
                            {counter}
                        </p>
                        <div className='add height-30px'
                            onClick={increaseCounter}>
                            <i class="fa-solid fa-plus "></i>
                        </div>
                    </div >
                </div>
            </div>
        </div>
    );
}

export default CartItem;
