// export default CartItem;
function CartItem(props) {
    const [quantity, setQuantity] = useState(0);

    const handleQuantityChange = (newQuantity) => {
        setQuantity(newQuantity);
        props.onQuantityChange(newQuantity);
    };

    return (
        <div className='CartItem'>
            <div className='CartItem-ItemHolder'>
                <img src={item} alt='item' className='CartItem-ItemHolder-Image margin-auto' />
            </div>
            <div className='CartItem-ItemHolder'>
                <p className='margin-auto'>
                    Cà Phê Đen
                </p>
            </div>
            <div className='CartItem-ItemHolder'>
                <p className='margin-auto'>
                    20000
                </p>
            </div>
            <div className='CartItem-ItemHolder margin-auto'>
                <div className='ChangeQuantity margin-auto'>
                    <ChangeQuantity quantity={quantity} onQuantityChange={handleQuantityChange} />
                </div>
            </div>
        </div>
    );
}
