import React from 'react';
import { useState } from 'react';

const lists = [
    {
        "id": 1,
        "name": "Vi"
    },
    {
        "id": 2,
        "name": "Vinh"
    },
    {
        "id": 3,
        "name": "Thục Vi"
    }
]



function Todo(props) {
    const [Checked, setChecked] = useState();

    const handleCheckedChange = () => {
        console.log(Checked)
    }

    return (
        <div style={{ padding: 32 }}>
            {
                lists.map(infor => (
                    <div key={infor.id} >
                        <input
                            type="checkbox"

                            onChange={() => {
                                setChecked(infor.id)
                                if (Checked === infor.id)
                                    Checked = true
                            }}
                        />{infor.name}
                    </div>
                ))


            }
            <button type='button' onClick={handleCheckedChange} > Submit</button>
        </div >
    );
}

export default Todo;
