import React, { useState, useEffect } from "react";
import Item from "./components/Item/Item";

const lists = [
    {
        id: 1,
        name: "Vi",
    },
    {
        id: 2,
        name: "Vinh",
    },
    {
        id: 3,
        name: "Thục Vi",
    },
];

function Todo(props) {
    const tabs = ['posts', 'albums', 'comments']
    const [posts, setPosts] = useState([])
    const [render, setRender] = useState(false)
    const [type, setType] = useState('')

    // const load = 
    const load = () => {
        useEffect(() => {
            fetch(`https://jsonplaceholder.typicode.com/${type}`)
                .then((respose) => {
                    return respose.json();
                })
                .then((post) => {
                    setPosts(post);
                })
        }, [type])
    }

    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [Checked, setChecked] = useState([]);

    var isChecked = (id) => {
        return Checked.includes(id)
    }

    const handleChecked = (id) => {
        setChecked(
            (prev) => {
                if (!isChecked(id)) {
                    return [...prev, id];
                } else {
                    return prev.filter((item) => {
                        return item !== id;
                    });
                }
            }
        );
    };

    const Submit = (event) => {
        event.preventDefault();
        const listUsers = lists.filter(user => {
            return Checked.includes(user.id)
        })

        console.log({
            "name": name,
            "address": { email: email, phoneNumber: phoneNumber },
            "checked": Checked,
            "listusers": listUsers,
        });
    };

    return (
        <div>
            <form
                onSubmit={Submit}
                style={{
                    display: "flex",
                    width: 400,
                    height: "auto",
                    textAlign: "center",
                    alignItems: "center",
                    flexDirection: "column",
                    border: "1px solid black",
                }}
            >
                <div
                    style={{
                        display: "flex",
                        flexDirection: "column",
                        justifyContent: "space-around",
                        height: 100,
                        paddingLeft: "16px",
                        paddingRight: "16px",
                    }}
                >
                    <input
                        type="text"
                        placeholder="Enter your name"
                        onChange={(e) => {
                            setName(e.target.value);
                        }}
                    />
                    <input
                        type="text"
                        placeholder="Enter your email"
                        onChange={(e) => {
                            setEmail(e.target.value);
                        }}
                    />
                    <input
                        type="text"
                        placeholder="Enter your phone number"
                        onChange={(e) => {
                            setPhoneNumber(e.target.value);
                        }}
                    />
                </div>
                <div
                    style={{
                        display: "flex",
                        flexDirection: "column",
                        width: 200,
                        textAlign: "start",
                        padding: 16,
                    }}
                >
                    {lists.map((infor) => (
                        <div key={infor.id}>
                            <input
                                type="checkbox"
                                checked={isChecked(infor.id)}
                                onChange={() => handleChecked(infor.id)}
                            />
                            {infor.name}
                        </div>
                    ))}
                </div>
                <button
                    type="submit"
                    style={{
                        padding: 8,
                        margin: 8,
                    }}
                >
                    Submit
                </button>
            </form>
            <div>
                <button
                    style={{ width: '200px', height: '100px' }}
                    onClick={() => {
                        setRender(!render)
                    }}
                >
                    Load
                </button>
                <div>
                    {
                        render && tabs.map((tab) => {
                            return (
                                <button
                                    key={tab}
                                    style={{ width: '100px', height: '50px' }}
                                    onClick={() => {
                                        setType(tab)
                                    }}
                                >
                                    {tab}
                                </button>
                            );
                        })
                    }
                </div>
                {
                    posts.map((post) => (
                        <li keys={post.id}>
                            {post.title ? post.title : post.body}
                        </li>
                    )
                    )
                }
            </div>
        </div >
    );
}

export default Todo;
