import React, { useState } from "react";
import Item from "./components/Item/Item";

const lists = [
    {
        id: 1,
        name: "Vi",
    },
    {
        id: 2,
        name: "Vinh",
    },
    {
        id: 3,
        name: "Thục Vi",
    },
];

function Todo(props) {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [Checked, setChecked] = useState([]);
    // const [name, setName] = useState('');

    // const arrayChecks = () => {
    //     Checked.map(arrayCheck => {
    //         ar
    //     })
    // }
    // const arrayAdd = () => {
    //     setChecked(arrayAdd(1))
    // }
    var isChecked = (id) => {
        return Checked.includes(id)
    }

    const handleChecked = (id) => {
        setChecked(
            (prev) => {
                // const isChecked = Checked.includes(id);
                if (!isChecked(id)) {
                    return [...prev, id];
                } else {
                    return prev.filter((item) => {
                        return item !== id;
                    });
                }
            }
        );
    };

    const Submit = (event) => {
        event.preventDefault();
        const listUsers = lists.filter(users => {
            Checked.includes(1)
        }, [])

        console.log({
            "name": name,
            "address": { email: email, phoneNumber: phoneNumber },
            "checked": Checked,
            "listusers": listUsers,
        });
    };

    return (
        <div>
            <form
                onSubmit={Submit}
                style={{
                    display: "flex",
                    width: 400,
                    height: "auto",
                    textAlign: "center",
                    alignItems: "center",
                    flexDirection: "column",
                    border: "1px solid black",
                }}
            >
                <div
                    style={{
                        display: "flex",
                        flexDirection: "column",
                        justifyContent: "space-around",
                        height: 100,
                        paddingLeft: "16px",
                        paddingRight: "16px",
                    }}
                >
                    <input
                        type="text"
                        placeholder="Enter your name"
                        onChange={(e) => {
                            setName(e.target.value);
                        }}
                    />
                    <input
                        type="text"
                        placeholder="Enter your email"
                        onChange={(e) => {
                            setEmail(e.target.value);
                        }}
                    />
                    <input
                        type="text"
                        placeholder="Enter your phone number"
                        onChange={(e) => {
                            setPhoneNumber(e.target.value);
                        }}
                    />
                </div>
                <div
                    style={{
                        display: "flex",
                        flexDirection: "column",
                        width: 200,
                        textAlign: "start",
                        padding: 16,
                    }}
                >
                    {lists.map((infor) => (
                        <div key={infor.id}>
                            <input
                                type="checkbox"
                                checked={isChecked(infor.id)}
                                onChange={() => handleChecked(infor.id)}
                            />
                            {infor.name}
                        </div>
                    ))}
                </div>
                <button
                    type="submit"
                    style={{
                        padding: 8,
                        margin: 8,
                    }}
                >
                    Submit
                </button>
            </form>
        </div>
    );
}

export default Todo;
