import React from 'react';
import { useState } from 'react';

const lists = [
    {
        "id": 1,
        "name": "Vi"
    },
    {
        "id": 2,
        "name": "Vinh"
    },
    {
        "id": 3,
        "name": "Thục Vi"
    }
]



function Todo(props) {
    const [checked, setChecked] = useState();

    const handleCheckedChange = () => {
        setChecked(infor.id)
    }

    return (
        <div style={{ padding: 32 }}>
            {
                lists.map(infor => (
                    <div key={infor.id} >
                        <input
                            type="checkbox"
                            checked={checked === infor.id}
                            onClick={handleCheckedChange}
                        />{infor.name}
                    </div>
                ))


            }
            <button type='button' > Submit</button>
        </div >
    );
}

export default Todo;
