// export default ChangeQuantity;
import React from 'react';

function ChangeQuantity(props) {
    const handleDecrease = () => {
        const newQuantity = Math.max(props.quantity - 1, 0);
        props.onQuantityChange(newQuantity);
    }

    const handleIncrease = () => {
        const newQuantity = props.quantity + 1;
        props.onQuantityChange(newQuantity);
    }

    return (
        <div className='ChangeQuantity ' >
            <div className='sub height-30px'
                onClick={handleDecrease}>
                <i class="fa-solid fa-minus"></i>
            </div>
            <p className='Quantity height-30px'>
                {props.quantity}
            </p>
            <div className='add height-30px'
                onClick={handleIncrease}>
                <i class="fa-solid fa-plus "></i>
            </div>
        </div >
    );
}

export default ChangeQuantity;
