function Item(props) {
    return (
        <div className='Item'>
            <img src={props.linkURL} alt="anh" className='Item-image margin-auto' />
            <div className='Item-content'>
                <p>
                    CAFE Đen
                </p>
                <p>
                    100000Đ
                </p>
            </div>
        </div>
    );
}

export default Item;
